import React, { useEffect, useState } from "react";
import logging from "./config/logging";
import axios from "axios";

const App: React.FunctionComponent = () => {
  const [loading, setLoading] = useState<boolean>(true);
  const [email, setEmail] = useState<string>("");

  const tryFetch = () => {
    axios
      .get("https://woodcock-more-arguably.ngrok-free.app/me", {
        withCredentials: true,
      })
      .then((res) => {
        logging.info(res.data.user, "/me");

        /**
         * if user has no nameID it means that he is not authenticated yet!
         * redirect to login for Okta auth
         */
        if (res.data.user.nameID) {
          setEmail(res.data.user.nameID);
          setLoading(false);
        } else {
          redirectToLogin(email);
        }
      })
      .catch((err) => {
        setLoading(false);
        logging.error(err, "/me");
      });
  }
  useEffect(() => {
    logging.info("checking authentication by calling /me");
    tryFetch();

  }, []);

  // Redirect to login
  const redirectToLogin = (email: string) => {

    axios
      .post("https://woodcock-more-arguably.ngrok-free.app/login", {
        withCredentials: true,
      }).then((res) => {
        logging.info(res.data, "/login");
      });
  };

  if (loading) return <p>loading...</p>;
  return <div>
    <input type="email" onChange={(e) => setEmail(e.target.value)}/>
    <div onClick={() => redirectToLogin(email)}>login</div>
    <div onClick={tryFetch}>try again</div>
  </div>;
};

export default App;
